package Helper;
import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import javafx.scene.image.Image;
import org.json.JSONArray;
import org.json.JSONObject;
import org.json.JSONTokener;
import java.io.*;
import java.nio.charset.StandardCharsets;
import java.text.SimpleDateFormat;
import java.util.Base64;
import java.util.Date;

public class FileManager {

    private static final String base64 = "data:image/png;base64,";
    private static final String base64s = "data:image/jpeg;base64,";
    private static final String jsonStr = "{"+
            "\"com\": \"z\"," +
            "\"from\": \"Rpi\"," +
            "\"images\": [{" +
                "\"base64\": \""+base64+"\"," +
                "\"id\": 12" +
            "},{" +
                "\"base64\": \""+base64s+"\"," +
                "\"id\": 10" +
            "}]" +
            "}";

    private String folderPath;
    private ObservableList observerList;

    public FileManager(){
        this.folderPath = "";
        this.observerList = FXCollections.observableArrayList();
    }

    public ObservableList getObserverList() {
        return observerList;
    }

    public void jsonToImage (String jsonStr) {
        File f;
        if(this.folderPath == "") {
            f = this.generateNewFolder();
            if (f == null)
                return;
        }
        else
            f = new File(this.folderPath);

        //temp holder
        jsonStr = this.jsonStr;

        JSONObject msg = new JSONObject(new JSONTokener(jsonStr));
        // tokenize the data
        if (msg.has("images")) {
            JSONArray arr = msg.getJSONArray("images");

            for(int i=0; i<arr.length(); i++){
                JSONObject obj = arr.getJSONObject(i);
                int id = obj.getInt("id");
                String image = obj.getString("base64");

                try {
                    observerList.add(id +".jpg");
                    String tmp = image.split(",")[1];
                    byte[] decodedString = Base64.getDecoder().decode(tmp.getBytes(StandardCharsets.UTF_8));
                    File image_f = new File(f, id+".jpg");
                    BufferedOutputStream bos = new BufferedOutputStream(new FileOutputStream(image_f));
                    bos.write(decodedString);
                    bos.flush();
                    bos.close();

                } catch (UnsupportedEncodingException e) {
                    // TODO Auto-generated catch block
                    e.printStackTrace();
                } catch (FileNotFoundException e) {
                    e.printStackTrace();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }

        }

    }

    public void saveMdf(String mapMdf, String exploredMdf){

        if(this.folderPath == "")
            this.generateNewFolder();

        // tokenize the data
        if (mapMdf != null && exploredMdf != null && this.folderPath != "") {

            try (PrintWriter out = new PrintWriter( this.folderPath + File.separator +"savedMdf.txt")) {
                out.println(mapMdf + "\n" + exploredMdf);
            } catch (FileNotFoundException e) {
                e.printStackTrace();
            }


        }
        else
            return;
    }

    private File generateNewFolder(){

        String fileName = "images";
        String timeStamp = new SimpleDateFormat("yyyy.MM.dd.HH.mm.ss").format(new Date());
        String path = System.getProperty("user.dir");
        path += File.separator + fileName + File.separator + timeStamp;
        System.out.println(path);

        File file = new File(path);
        //Creating the directory
        boolean isCreated = file.mkdirs();
        if(isCreated){
            System.out.println("Directory created successfully");
            this.folderPath = path;
            return file;
        }else{
            System.out.println("Could not create specified directory");
            return null;
        }
    }

    public Image generateImageFromPath(String img){
        try {
            return new Image(new FileInputStream(this.folderPath +  File.separator + img));
        } catch (FileNotFoundException e) {
            e.printStackTrace();
            return null;
        }
    }



}
